mapper.select:
SELECT d.*, e.name as emoji_name, e.emoji_image 
FROM diary d
LEFT JOIN emoji e ON d.emoji_id = e.id 
WHERE d.user_id = #{userId} 
AND DATE(d.created_at) = #{targetDate}

SELECT DATE_FORMAT(d.created_at, '%Y-%m') as month, e.id as emoji_id, e.name as emoji_name, e.emoji_image, COUNT(*) as count FROM diary d JOIN emoji e ON d.emoji_id = e.id WHERE d.user_id = #{userId}
AND d.created_at >= #{startDate}
AND d.created_at <= #{endDate}
GROUP BY DATE_FORMAT(d.created_at, '%Y-%m'), e.id, e.name, e.emoji_image ORDER BY month DESC, count DESC
